questionsList.script:
multCount = Retrieve("events/countMultQuadEvent/mult");
quadCount = Retrieve("events/countMultQuadEvent/quad");
Debug();

CommentIf ("$quadCount > $quadriert", "Der 'quadriert'-Wert ist zu klein.");
CommentIf ("$quadCount < $quadriert", "Der 'quadriert'-Wert ist zu groß.");

CommentIf ("$multCount > $multipliziert", "Der 'multipliziert'-Wert ist zu klein.");
CommentIf ("$multCount < $multipliziert", "Der 'multipliziert'-Wert ist zu groß.");

Check ("$quadCount = $quadriert");
Check ("$multCount = $multipliziert");
Ok();

ze = Retrieve("events/zwischenErgebnis[seqnr = $anzahlSchritte]/zwErg");
Debug();

CommentIf ("$ze < $Zwischenergebnis", "Das Zwischenergebnis ist zu groß.");
CommentIf ("$ze > $Zwischenergebnis", "Das Zwischenergebnis ist zu klein.");

Check ("$ze = $Zwischenergebnis");
Ok();

ze = Retrieve("events/zwischenErgebnis[seqnr = $anzahlSchritte]/zwErg");
Debug();

CommentIf ("$ze < $Zwischenergebnis", "Das Zwischenergebnis ist zu groß.");
CommentIf ("$ze > $Zwischenergebnis", "Das Zwischenergebnis ist zu klein.");

Check ("$ze = $Zwischenergebnis", "Hinweis: Hast du an den Modulo gedacht?");
Ok();

myexp = Retrieve ("events/exponentToBinary/binaryExponent");
zwergevent = Retrieve ("events/zwischenErgebnis");
zwergcount = Retrieve ("count(/)", zwergevent);

myErg = Retrieve ("events/zwischenErgebnis[seqnr=$zwergcount]/zwErg");

Debug();

CommentIf ("$myErg < $Zwischenergebnis", "Das Zwischenergebnis ist zu groß.");
CommentIf ("$myErg > $Zwischenergebnis", "Das Zwischenergebnis ist zu klein.");

Check ("$Binärdarstellung_Exponent = $myexp", "Die Binärdarstellung stimmt nicht.");
Check ("$myErg = $Ergebnis", "Das Ergebnis ist nicht korrekt.");
Ok();
