questionsList.script:
myt1 = Retrieve("events/permutateSubstringEvent[seqnr=1]/permutatedSubstring");
myt3 = Retrieve("events/permutateSubstringEvent[seqnr=3]/permutatedSubstring");
myt7 = Retrieve("events/permutateSubstringEvent[seqnr=7]/permutatedSubstring");
Debug();
Check ("$myt1 = $t1", "t1 stimmt nicht!");
Check ("$myt3 = $t3", "t3 stimmt nicht!");
Check ("$myt7 = $t7", "t7 stimmt nicht!");
Ok();

myc1 = Retrieve("events/xorResultEvent[seqnr=1]/xorResult");
myc3 = Retrieve("events/xorResultEvent[seqnr=3]/xorResult");
myc7 = Retrieve("events/xorResultEvent[seqnr=7]/xorResult");
Debug();
Check ("$myc1 = $c1", "c1 stimmt nicht!");
Check ("$myc3 = $c3", "c3 stimmt nicht!");
Check ("$myc7 = $c7", "c7 stimmt nicht!");
Ok();

myi1 = Retrieve("events/nextIEvent[seqnr=1]/nextI");
myi2 = Retrieve("events/nextIEvent[seqnr=2]/nextI");
myi6 = Retrieve("events/nextIEvent[seqnr=6]/nextI");
Debug();
Check ("$myi1 = $I1", "I1 stimmt nicht!");
Check ("$myi2 = $I2", "I2 stimmt nicht!");
Check ("$myi6 = $I6", "I6 stimmt nicht!");
Ok();

rounds = Retrieve ("count(events/permutatedEvent)");
Debug();
CommentIf("$rounds > $Anzahl", "CFB benötigt mehr Runden!");
CommentIf("$rounds < $Anzahl", "CFB benötigt weniger Runden!");
Check("$rounds = $Anzahl");
Ok();
