questionsList.script:
myt1 = Retrieve("events/permutateSubstringEvent[seqnr=1]/permutatedSubstring");
myc1 = Retrieve("events/xorResultEvent[seqnr=1]/xorResult");

Debug();

Check ("$myt1 = $t1", "t1 stimmt nicht!");
Check ("$myc1 = $c1", "c1 stimmt nicht!");
Ok();

myc2 = Retrieve("events/xorResultEvent[seqnr=2]/xorResult");
myt2 = Retrieve("events/permutateSubstringEvent[seqnr=2]/permutatedSubstring");

Debug();

Check ("$myt2 = $t2", "Der Wert von t2 stimmt nicht.");
Check ("$myc2 = $c2", "Der Wert von c2 stimmt nicht.");
Ok();

myc4 = Retrieve("events/xorResultEvent[seqnr=4]/xorResult");
myt4 = Retrieve("events/permutateSubstringEvent[seqnr=4]/permutatedSubstring");

myO3 = Retrieve("events/permutatedEvent[seqnr=3]/permutated");
myc3 = Retrieve("events/xorResultEvent[seqnr=3]/xorResult");

myI3 = Retrieve("events/nextIEvent[seqnr=3]/nextI");
myI4 = Retrieve("events/nextIEvent[seqnr=4]/nextI");

Debug();

CommentIf ("$myO3 != $O3", "Der Wert von O3 stimmt nicht. Hinweis: I_3 = %s", myI3);
CommentIf ("$myt4 != $t4", "Der Wert von t4 stimmt nicht. Hinweis: I_4 = %s", myI4);

Check ("$myO3 = $O3");
Check ("$myc3 = $c3", "Der Wert von c3 stimmt nicht.");
Check ("$myt4 = $t4");
Check ("$myc4 = $c4", "Der Wert von c4 stimmt nicht.");
Ok();

rounds = Retrieve ("count(events/permutatedEvent)");
Debug();
CommentIf("$rounds > $Anzahl", "CFB benötigt mehr Runden!");
CommentIf("$rounds < $Anzahl", "CFB benötigt weniger Runden!");
Check("$rounds = $Anzahl");
Ok();
