xA = Retrieve ("events/AEvent/A");
xB = Retrieve ("events/BEvent/B");
Debug();

Check ("$A = $xA", "A ist falsch");
Check ("$B = $xB", "B ist falsch");
Ok();

x1 = Retrieve ("events/KBEvent/KB");
x2 = Retrieve ("events/KAEvent/KA");
Debug();

CommentIf ("$K > $p", "Der Wert von K kann nicht größer als p = %s sein.", p);
CommentIf ("$K > $x1", "Dein Lösungswert ist zu groß.");
CommentIf ("$K < $x1", "Dein Lösungswert ist zu klein.");
Check ("$x1 = $x2", "Die K-Werte stimmen nicht überein. Da ist wohl etwas schief gelaufen ;-)");
Check ("$x1 = $K");
Ok();

