questionsList.script:
myI2 = Retrieve("events/O0Event/O0");
myI3 = Retrieve("events/nextiEvent[seqnr=1]/nexti");

myc1 = Retrieve("events/c0Event/c0");
myc3 = Retrieve("events/ciEvent[seqnr=2]/ci");

Debug();

Check ("$c1 = $myc1", "Der Wert von c1 stimmt nicht.");
Check ("$I2 = $myI2", "Der Wert von I2 stimmt nicht.");
Check ("$I3 = $myI3", "Der Wert von I3 stimmt nicht.");
Check ("$c3 = $myc3", "Der Wert von c3 stimmt nicht.");
Ok();

mycipher = Retrieve ("events/cipherEvent/cipher");
myt1 = Retrieve ("events/t0Event/t0");

Debug();

CommentIf ("$cipher_text != $mycipher", "Mit dem angegebenen Initial_Vector würde die verschlüsselte Nachricht %s entstehen", mycipher);

Check ("$myt1 = $t1", "Der Wert von t1 ist nicht korrekt. Hinweis: c1 = m1 XOR t1");
Check("$cipher_text = $mycipher");

Ok();

mycipher = Retrieve ("events/cipherEvent/cipher");
Debug();

CommentIf ("$cipher_text != $mycipher", "Mit dieser Permutationsfunktion wäre der Ciphertext %s. Hinweis: Berechne die Werte von t bzw. O und leite daraus die Werte von I ab.", mycipher);

Check ("$cipher_text = $mycipher");

Ok();

mycipher = Retrieve ("events/cipherEvent/cipher");
Debug();

CommentIf ("$mycipher != $cipher_text", "Diese Nachricht würde verschlüsselt den Ciphertext %s ergeben.", mycipher);

Check ("$mycipher = $cipher_text");

Ok();

mym1 = Retrieve("events/m0Event/m0");
mym3 = Retrieve("events/miEvent[seqnr=2]/mi");
mym7 = Retrieve("events/miEvent[seqnr=6]/mi");

myMiEvent = Retrieve("events/miEvent");
myAnzahl = Retrieve("count(/)", myMiEvent);

Debug();
Check ("$m1 = $mym1", "Der m1-Wert ist nicht korrekt.");
Check ("$m3 = $mym3", "Der m3-Wert ist nicht korrekt.");
Check ("$m7 = $mym7", "Der m7-Wert ist nicht korrekt.");
Check ("$Anzahl_der_Blöcke = $myAnzahl +1", "Die Anzahl der Blöcke stimmt leider nicht.");
Ok();

myt1 = Retrieve ("events/t0Event/t0");
myc2 = Retrieve ("events/ciEvent[seqnr=1]/ci");
myc3 = Retrieve ("events/ciEvent[seqnr=2]/ci");

Debug();

Check ("$myt1 = $t1", "Mit diesem Initialvektor kommt ein anderer Wert für t1 raus.");
Check ("$myc2 = $c2", "Für diesen Initialvektor stimmt der c2-Wert nicht.");
Check ("$myc3 = $c3", "Für diesen Initialvektor stimmt der c3-Wert nicht.");

Ok();

myc1 = Retrieve ("events/c0Event/c0");
myt1 = Retrieve ("events/t0Event/t0");
myt3 = Retrieve ("events/tiEvent[seqnr=2]/ti");

Debug();

Check ("$myc1 = $c1", "Fehler in der Aufgabenstellung.");
Check ("$myt1 = $t1", "Der Wert von t1 stimmt nicht.");
Check ("$myt3 = $t3", "Der Wert von t3 stimmt nicht.");

Ok();
