questionsList.script:
myn = Retrieve ("events/nEvent/n");
myPhi = Retrieve ("events/phiEvent/phi");
myeValid = Retrieve ("events/eIsValidEvent/eIsValid");

Debug();

Check ("$myn = $n", "Der Wert von n ist leider nicht korrekt.");
Check ("$myPhi = $phi", "Der Wert von phi ist leider nicht korrekt.");
Check ("$myeValid", "Dieser e-Wert passt leider nicht.");

Ok();

myd = Retrieve ("events/dEvent/d");
Debug();

CommentIf ("$d_ist_passend = 'true'", "Tipp: Berechne e*d mod phi(n).");

Check ("$d != $myd", "Fehler in der Aufgabenstellung.");
Check ("$d_ist_passend = 'false'");

Ok();

myc = Retrieve ("events/cEvent/c");
mym = Retrieve ("events/mEvent/m");
Debug();

Check ("$Ciphertext = $myc");

Ok();

myc = Retrieve ("events/cEvent/c");
mye = Retrieve ("events/eEvent/e");
myd = Retrieve ("events/dEvent/d");

Debug();

Check ("$myc = $Ciphertext", "Fehler in der Aufgabenstellung.");
Check ("$Klartext = $Nachricht");

Ok();
